mapper.update:
UPDATE news
SET title = #{title},
    content = #{content},
    category_id = #{categoryId},
    updated_by = #{updatedBy}
WHERE id = #{id}

mapper.select:
SELECT n.id, n.title, n.content, n.category_id, n.create_time, n.update_time, n.created_by, n.updated_by FROM news n LEFT JOIN news_category nc ON n.category_id = nc.id
AND n.title LIKE CONCAT('%', #{title}, '%')
AND n.category_id = #{categoryId}
AND n.create_time >= #{startTime}
AND n.create_time <= #{endTime}
ORDER BY n.create_time DESC
LIMIT #{pageNum}, #{pageSize}

SELECT COUNT(*)
FROM news n


        AND n.title LIKE CONCAT('%', #{title}, '%')


        AND n.category_id = #{categoryId}


        AND n.create_time >= #{startTime}


        AND n.create_time <= #{endTime}

